import React, { useState, useEffect, useContext } from "react";
import { StateContext } from "../Context/StateContext";
import { Link } from "react-router-dom";



function UploadVideos  () {

const[tempSource,setTempSource] = useState("")
const[displayVideo,setDisplayVideo] = ("display:block")
const[displayYouTubeVideo,setDisplayYouTubeVideo] = ("display:none")


// https://www.youtube.com/embed/8YhAFBOSk1U


function getTemporaryUrl(e){
    const { name, value,files } = e.target;
switch (name) {
  case "videoUrl":
         setTempSource(value)
if(value.includes("youtube")){
setDisplayYouTubeVideo("dis")
setDisplayVideo()
}else{

setIframe(false)

}

    break;

  case "fileUpload":
         setTempSource(URL.createObjectURL(files[0]))

    break;


  default:
    break;
}




}

function handleUpLoadVideo(){





}


          //   </div>
          // </div>
          // <input
          //   type="file"
          //   name="fileUpload"
          //   accept="video/mp4"
          //   ref="filesUploadVideo"
          //   @change="localFiles"
          //   v-show="false"
          // />





    return (
 <React.Fragment> 
<div className="container p-3 my-3   videoUpload-container">

<form onSubmit={handleUpLoadVideo} >
{ (tempSource.length>0) &&   ( <div>

             <video style={{displayVideo}} width="500" heigth="100" controls>
               <source src={tempSource} type="video/mp4" />
             <source src={tempSource} type="video/ogg" />
        Your browser does not support HTML video.
       </video>


<iframe width="560" style={{displayYouTubeVideo}} height="315" src={tempSource} title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>


</div>



)


}



<div><input type="text" name="videoUrl" className="form-control"
  onChange={getTemporaryUrl}
 placeholder="Enter video Url" />
</div>
<br />
<span>Or</span>

<br />
<br />
<div>
 <input
className="form-control" 
            type="file"
            name="fileUpload"
            accept="video/mp4"
            onChange={getTemporaryUrl}
id="inputGroupFile02"
          />


</div>
    




<div className=" upload-video"><button className=" btn btn-success " type="submit" >UpLoad Video</button>
</div>



</form>

</div>
</React.Fragment>

  );
}
 
export default  UploadVideos;
