import React, { useState, useEffect, useContext } from "react";
import { StateContext } from "../Context/StateContext";
import { Link } from "react-router-dom";

function UploadVideos() {
	const {uploadVideo } = useContext(StateContext);
  const [tempOtherVideoSource, setOtherVideoTempSource] = useState("");
  const [tempYoutubeSource, setYoutubeTempSource] = useState("");
const[tempVideoSource,setVideoTempSource] = useState("")
    const[videoTitle,setVideoTitle] = useState("")
  const [displayVideo, setDisplayVideo] = useState("block");
  const [displayYouTubeVideo, setDisplayYouTubeVideo] = useState("none");

  // useEffect(() => displayVideoContainer(), [loginUser]);

  // https://www.youtube.com/embed/8YhAFBOSk1U
  // https://www.w3schools.com/html/mov_bbb.mp4
  // https://www.youtube.com/embed/FCUk7rIBBAE

  function getTemporaryUrl(e) {
    const { name, value, files } = e.target;
    switch (name) {
      case "videoUrl":
        if (value.includes("youtu")) {
          handleYoutubeUrl(value);
        } else {
          setDisplayYouTubeVideo("none");
          setDisplayVideo("block");
          setOtherVideoTempSource(value);
setVideoTempSource(value)
        }

        break;

      case "fileUpload":
        setOtherVideoTempSource(URL.createObjectURL(files[0]));
setVideoTempSource(URL.createObjectURL(files[0]))

        break;

      default:
        break;
    }
    e.preventDefault();
  }


function handleTitleInput(e){
    const { name, value, files } = e.target;

setVideoTitle(value)


}


  function handleYoutubeUrl(videoSourc) {
    setDisplayYouTubeVideo("block");
    setDisplayVideo("none");
    if (videoSourc.includes("embed") > 0) {
setVideoTempSource(videoSourc)

      return setYoutubeTempSource(videoSourc);
    } else {
      const urlParams = "youtu.be/";
      const indexOfvideoId = url.search(urlParams) + urlParams.length;
      const videoId = url.substr(indexOfvideoId, 11);
      const videoUrl = `https://www.youtube.com/embed/${videoId}`;
      console.log(videoUrl, "working");
setVideoTempSource(videoUrl)

      return setYoutubeTempSource(videoUrl);
    }
  }

  function handleUploadVideo() {


uploadVideo(videoTitle,)

}

  //   </div>
  // </div>
  // <input
  //   type="file"
  //   name="fileUpload"
  //   accept="video/mp4"
  //   ref="filesUploadVideo"
  //   @change="localFiles"
  //   v-show="false"
  // />

  return (
    <React.Fragment>
      <div className="container p-3 my-3   videoUpload-container">
        <form onSubmit={handleUploadVideo}>
          <div className="videoFrame">
            {(tempOtherVideoSource.length > 0 || tempYoutubeSource.length > 0) && (
              <div>
                <div style={{ display: displayVideo }}>
                  <video width="500" heigth="315" controls>
                    <source src={tempOtherVideoSource} type="video/mp4" />
                    <source src={tempOtherVideoSource} type="video/ogg" />
                    Your browser does not support HTML video.
                  </video>
                </div>
                <div style={{ display: displayYouTubeVideo }}>
                  <iframe
                    width="560"
                    height="315"
                    src={tempYoutubeSource}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowfullscreen
                  ></iframe>
                </div>
              </div>
            )}
          </div>
          <div>
            <input
              type="text"
              name="videoUrl"
              className="form-control"
              onChange={getTemporaryUrl}
              placeholder="Enter video Url"
            />
          </div>
          <br />
          <span>Or</span>

          <br />
          <br />
          <div>
            <input
              className="form-control"
              type="file"
              name="fileUpload"
              accept="video/mp4"
              onChange={getTemporaryUrl}
              id="inputGroupFile02"
            />
          </div>

          <div className="required-video-details">
            <input type="text" name="videoTitle" onChange="" />
            {/* <input type="text"  name="videoType"  />
<input type="text"  name="videoTitle"  />
<input type="text"  name="videoTitle"  />
<input type="text"  name="videoTitle"  /> */}
          </div>

          <div className=" upload-video">
            <button className=" btn btn-success " type="submit">
              UpLoad Video
            </button>
          </div>
        </form>
      </div>
    </React.Fragment>
  );
}

export default UploadVideos;
