import React, { useContext, useState,useEffect } from "react";
import { StateContext } from "../Context/StateContext";

import { Link } from "react-router-dom";


function SearchVideos() {
  const { allVideos } = useContext(StateContext);
const [videosTitles,setVideosTitles] = useState([]);
const [searchedVideo,SetSearchedVideo] =  useState("")
const [searchResult,setSearchResult]=useState([])

  useEffect(() => getVideosTitles(),[allVideos]);


function getVideosTitles(){
const videosTitles = allVideos.map(video=>video.videoTitle)
setVideosTitles(videosTitles)
}


function handleInputChange(e){
const{value} = e.target
SetSearchedVideo(value)
handleSumitSearch()
}


function handleSumitSearch(){

 let filteredSearchedVideo = videosTitles.filter((video) => {
        const trimmedSearchValue =searchedVideo
          .replace(/\s+/g, "")
          .toLowerCase();
        return (
          video.toLowerCase().includes(trimmedSearchValue)
        );
      });
setSearchResult(filteredSearchedVideo)
}



  return (
    <React.Fragment>
<form >
<div className="search">

<input type="text" className="input-control" onChange={handleInputChange} placeholder="Search for Videos" />
<button onSubmit={handleSumitSearch}>Search</button>
</div>



</form>
 </React.Fragment>
  );
}

export default SearchVideos;
