import React, { useState, useEffect, useContext } from "react";
import { StateContext } from "../Context/StateContext";
import { Link } from "react-router-dom";

function UploadVideos() {
  const [tempSource, setTempSource] = useState("");
  const [tempYoutubeSource, setYoutubeTempSource] = useState("");

  const [displayVideo, setDisplayVideo] = useState("block");
  const [displayYouTubeVideo, setDisplayYouTubeVideo] = useState("none");

  // https://www.youtube.com/embed/8YhAFBOSk1U
  // https://www.w3schools.com/html/mov_bbb.mp4
// https://www.youtube.com/embed/FCUk7rIBBAE

  function getTemporaryUrl(e) {
    const { name, value, files } = e.target;
    switch (name) {
      case "videoUrl":
        if (value.includes("youtu")) {
       handleYoutubeUrl (value)

        } else {
          setDisplayYouTubeVideo("none");
          setDisplayVideo("block");
        setTempSource(value);

        }


        break;

      case "fileUpload":
        setTempSource(URL.createObjectURL(files[0]));

        break;

      default:
        break;
    }
  }

function handleYoutubeUrl (url){
setDisplayYouTubeVideo("block");
setDisplayVideo("none");
if(url.includes("embed")>0){
return setYoutubeTempSource(url)

}
else{
const urlParams ="youtu.be/"
const indexOfvideoId =  url.search(urlParams) + urlParams.length
const videoId = url.substr(indexOfvideoId,11)
const videoUrl = `https://www.youtube.com/embed/${videoId}`
console.log(videoUrl,"working");
return setYoutubeTempSource(videoUrl)

}



}



  function handleUpLoadVideo() {}

  //   </div>
  // </div>
  // <input
  //   type="file"
  //   name="fileUpload"
  //   accept="video/mp4"
  //   ref="filesUploadVideo"
  //   @change="localFiles"
  //   v-show="false"
  // />

  return (
    <React.Fragment>
      <div className="container p-3 my-3   videoUpload-container">
        <form onSubmit={handleUpLoadVideo}>
<div className="videoFrame"></div>
          {(tempSource.length > 0 || tempYoutubeSource.length > 0) && (
            <div >
              <div style={{ display: displayVideo }}>
                <video width="500" heigth="315" controls>
                  <source src={tempSource} type="video/mp4" />
                  <source src={tempSource} type="video/ogg" />
                  Your browser does not support HTML video.
                </video>
              </div>
              <div  style={{display:displayYouTubeVideo}} >
              <iframe
                width="560"
                height="315"
                src={tempYoutubeSource}
                title="YouTube video player"
                 frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowfullscreen
              ></iframe>
              </div>
            </div>
          )}

          <div>
            <input
              type="text"
              name="videoUrl"
              className="form-control"
              onChange={getTemporaryUrl}
              placeholder="Enter video Url"
            />
          </div>
          <br />
          <span>Or</span>

          <br />
          <br />
          <div>
            <input
              className="form-control"
              type="file"
              name="fileUpload"
              accept="video/mp4"
              onChange={getTemporaryUrl}
              id="inputGroupFile02"
            />
          </div>

          <div className=" upload-video">
            <button className=" btn btn-success " type="submit">
              UpLoad Video
            </button>
          </div>
        </form>
      </div>
    </React.Fragment>
  );
}

export default UploadVideos;
