import React, { useState, useEffect, useContext } from "react";
import { StateContext } from "../Context/StateContext";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";

function Home() {
  const { userData, allVideos } = useContext(StateContext);

  return (
    <React.Fragment>
      <div className="home-container">
        <h1>
          welcome &nbsp;
          {userData.userName}
        </h1>          <div className="home-videos-container container bg-success">

        {allVideos.map((video) => (
            <div className="home-videos" key={video.videoId}>
              <div className="home-video">
             {/* (!video.videoSource.includes("youtube")  &&   <video width="400" heigth="100" controls>
                  <source
                    src={video.videoSource}
                    type="video/mp4"
                  />
                  <source
                    src={video.videoSource}
                    type="video/ogg"
                  />
                  Your browser does not support HTML video.
                </video>) */}

     <iframe
                    width="550"
                    height="300"
                    src={video.videoSource}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowfullscreen
                  ></iframe>

              </div>
              <div className="home-video-details">
                <p>{video.videoTitle}</p>
                <p>user</p>
                <p>video duration</p>
                <p>likes</p>
                <p>comments</p>
                <p>views</p>
              </div>
            </div>
         
        ))}
 </div>
      </div>
    </React.Fragment>
  );
}

export default Home;
