import { StateContext } from "../Context/StateContext";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";

function ViewedVideo() {
  const { allVideos,viewedVideo } = useContext(StateContext);
  const [userName, setUserName] = useState("");
  // const [viewedVideo, setViewedVideo] = useState("");
  const [userNotFound, setUserNotFound] = useState(false);
  const history = useHistory();

  // useEffect(() => redirectUser(), [loginUser]);

  return (
    <div className="viewed-video-container">
      <div className="viewed-video-box">
        <div className="video-frame">

 { !viewedVideo.videoSource.includes("youtube")  &&   <video width="550" heigth="300" controls>
                  <source
                    src={viewedVideo.videoSource}
                    type="video/mp4"
                  />
                  <source
                    src={viewedVideo.videoSource}
                    type="video/ogg"
                  />
                  Your browser does not support HTML video.
                </video>}

    { viewedVideo.videoSource.includes("youtube") && <iframe
                    width="550"
                    height="300"
                    src={viewedVideo.videoSource}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowfullscreen
                  ></iframe>
}



</div>
        <div className="details"></div>
      </div>

      <div className="all-video-container">


</div>
    </div>
  );
}

export default ViewedVideo;
