import React, { useState, useEffect, useContext } from "react";
import { StateContext } from "../Context/StateContext";
import { Link } from "react-router-dom";
import axios from "axios";

import { v4 as uuidv4 } from "uuid";

function UploadVideos() {
  const { handleUpdataData, user } = useContext(StateContext);
  const [tempOtherVideoSource, setTempOtherVideoSource] = useState("");
  const [tempYoutubeVideoSource, setTempYoutubeVideoSource] = useState("");
  // const [videoSource,setVideoSource] = useState("")
  const [file, setFile] = useState("");
  const [fileName, setFileName] = useState("Choose a file");
  const [uploadProgress, setUploadProgress] = useState({});
  const [videoSource, setvideoSource] = useState("");
  const [videoTitle, setVideoTitle] = useState("");
  const [displayVideo, setDisplayVideo] = useState("block");
  const [displayYouTubeVideo, setDisplayYouTubeVideo] = useState("none");
const [videoId,setVideoId]=  useState("");
  // useEffect(() => displayVideoContainer(), [loginUser]);

  // https://www.youtube.com/embed/8YhAFBOSk1U
  // https://www.w3schools.com/html/mov_bbb.mp4
  // https://www.youtube.com/embed/FCUk7rIBBAE

  function getTemporaryUrl(e) {
const video_Id = uuidv4();   
 const { name, value, files } = e.target;
  setVideoId(video_Id);


    switch (name) {
      case "videoSource":
        if (value.includes("youtu")) {
          handleYoutubeUrl(value);
        } else {
          setDisplayYouTubeVideo("none");
          setDisplayVideo("block");
          setTempOtherVideoSource(value);
          setvideoSource(value);
        }

        break;

      case "fileUpload":
        const videoTypeIndex = 6
        const videoType = files[0].type.slice(videoTypeIndex)
         setVideoTitle(files[0].name.replace(`.${videoType}`,""));
        setTempOtherVideoSource(URL.createObjectURL(files[0]));
        setvideoSource(`/stream_videos/${video_Id}`);
        setFile(files[0]);

        break;

      default:
        break;
    }
  }

  function handleTitleInput(e) {
    const { name, value, files } = e.target;

    setVideoTitle(value);
  }

  function handleYoutubeUrl(videoFromYoutube) {
    setDisplayYouTubeVideo("block");
    setDisplayVideo("none");
    if (videoFromYoutube.includes("embed") > 0) {
      setvideoSource(videoFromYoutube);

      return setTempYoutubeVideoSource(videoFromYoutube);
    } else {
      const urlParams = "youtu.be/";
      const indexOfvideoId =videoFromYoutube.search(urlParams) + urlParams.length;
      const youTubeVideoIdLength = 11;
      const videoId = videoFromYoutube.substr(indexOfvideoId, youTubeVideoIdLength);
      const videoUrl = `https://www.youtube.com/embed/${videoId}`;
      setvideoSource(videoUrl);

      return setTempYoutubeVideoSource(videoUrl);
    }
  }

  const handleUploadVideoDetails = async (e) => {
    e.preventDefault();
    const videoDetails = {
      videoTitle,
     videoSource,
      videoId,
    };
        try {
        const response = axios.post(
          `/upload_video/video_details/${user.userName}/${user._id}`,
          videoDetails
        );

    if (file) {
handleUploadVideoLocally (videoId)
    } 
      } catch (err) {
        console.log(err);
      }


handleUpdataData(user._id,user.userName,true)

  };




const handleUploadVideoLocally = async (videoId)=>{
  const formData = new FormData();
    formData.append("file", file);
      try {
        const response = await axios.post(
          `/upload_video/${videoId}`,
          formData , {
                    headers: {
                      "content-Type": "multipart/form-data",
                    },
                  }
        );

        const { fileName, filePath } = response.data;
        setUploadfile({ fileName, filePath });
      } catch (err) {
        if (err.response.status === 500) {
          console.log("server-problem");
        } else {
          console.log(err.response.status,5000);
        }
      }
} 



  return (
    <React.Fragment>
      <div className="container p-3 my-3   videoUpload-container">
<h1>{videoId}</h1>
        <form onSubmit={handleUploadVideoDetails}>
          <div className="videoFrame">
            {videoSource.length > 0 && (
              <div>
                <div style={{ display: displayVideo }}>
                  <video width="500" heigth="315" controls>
                    <source src={tempOtherVideoSource} type="video/mp4" />
                    <source src={tempOtherVideoSource} type="video/ogg" />
                    Your browser does not support HTML video.
                  </video>
                </div>
                <div style={{ display: displayYouTubeVideo }}>
                  <iframe
                    width="560"
                    height="315"
                    src={tempYoutubeVideoSource}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                  ></iframe>
                </div>
              </div>
            )}
          </div>

          {videoSource.length <= 0 && (
            <div>
              <div>
                <input
                  type="text"
                  name="videoSource"
                  className="form-control"
                  onChange={getTemporaryUrl}
                  placeholder="Enter video Url"
                />
              </div>

              <br />
              <span>Or</span>

              <br />
              <br />
              <div>
                <input
                  className="form-control"
                  type="file"
                  name="fileUpload"
                  accept="video/mp4"
                  onChange={getTemporaryUrl}
                  id="inputGroupFile02"
                />
              </div>
            </div>
          )}

          {videoSource.length > 0 && (
            <div className="required-video-details">
              <input
                type="text"
                name="videoTitle"
                onChange={handleTitleInput}
                placeholder="Enter Video Title"
value={videoTitle}
              />
              {/* <input type="text"  name="videoType"  />
<input type="text"  name="videoTitle"  />
<input type="text"  name="videoTitle"  />
<input type="text"  name="videoTitle"  /> */}
            </div>
          )}

          <div className=" upload-video">
            <button className=" btn btn-success btn-block" type="submit">
              UpLoad Video
            </button>
          </div>
        </form>
      </div>
    </React.Fragment>
  );
}

export default UploadVideos;
